-- Listage de la structure de la base pour sada
DROP DATABASE IF EXISTS `sada`;
CREATE DATABASE IF NOT EXISTS `sada` /*!40100 DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci */ /*!80016 DEFAULT ENCRYPTION='N' */;
USE `sada`;

-- Listage de la structure de table sada. articles
DROP TABLE IF EXISTS `articles`;
CREATE TABLE IF NOT EXISTS `articles` (
  `id` int NOT NULL AUTO_INCREMENT,
  `description` varchar(500) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT '',
  `article` text NOT NULL,
  `data` datetime NOT NULL,
  `image` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT '0',
  `titre` varchar(200) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL,
  `type` int DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_articles_type` (`type`),
  CONSTRAINT `FK_articles_type` FOREIGN KEY (`type`) REFERENCES `type` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

-- Listage des données de la table sada.articles : ~14 rows (environ)
INSERT INTO `articles` (`id`, `description`, `article`, `data`, `image`, `titre`, `type`) VALUES
	(1, 'ت مواقع التواصل الاجتماعي، و لحظة تبرعه بالدم لفائدة ضحايا الزلزال و عناقه للمصابين و تقبيله لرؤوس المرضى و اطمئنانه على رعيته، لقطات ستبقى خالدة في أذهان و قلوب كل المغاربة لقائد عظيم، لملك مواطن، لإنسان خدوم، لمغربي نموذج.', 'صوره غصت مواقع التواصل الاجتماعي، و لحظة تبرعه بالدم لفائدة ضحايا الزلزال و عناقه للمصابين و تقبيله لرؤوس المرضى و اطمئنانه على رعيته، لقطات ستبقى خالدة في أذهان و قلوب كل المغاربة لقائد عظيم، لملك مواطن، لإنسان خدوم، لمغربي نموذج.', '2023-09-13 13:51:41', 'https://ar.hibapress.com/wp-content/uploads/2023/09/IMG_20230913_100831.jpg', 'قائد و أمة.. معا سنتجاوز بإذن الله محن الأزمة', 2),
	(4, ' غاب النجم البرتغالي كريستيانو رونالدو لاعب فريق النصر السعودي،', ' غاب النجم البرتغالي كريستيانو رونالدو لاعب فريق النصر السعودي،\r\n     عن اللائحة التي تضم أسماء المرشحين بجائزة الكرة الذهبية السنوية، المقدمة من مجلة فرانس فوتبول الفرنسية.ويعتبر غياب كريستيا..', '2023-09-13 13:51:41', './image/ph.jpeg', 'ويعتبر غياب كريست', 4),
	(5, 'بورصة الدار البيضاء تشهد تداولات سلبية', 'بورصة الدار البيضاء تشهد تداولات سلبية', '2023-09-14 13:25:00', './image/téléchargement1.jpeg', '     وزارة التجهيز والماء تنفي تضرر ', 2),
	(8, ' فت وزارة التجهيز والماء تضرر بعض السدود بإقليم الحوز جراء الزلزال الذي شهدته عدة مناطق بالمملكة، يوم الجمعة الماضي، لا سيما سدود لالا تاكركوست، ويعقوب المنصور، وأبو العباس السبتي، مؤكدة أن السدود المذكورة توجد في حالة جيدة وتضطلع بوظائفها بشكل عادي.', '  وأهابت وزارة التجهيز والماء بجميع المواطنات والمواطنين تحري الدقة والمصداقية في ما يتم تداوله من أخبار حول تداعيات الزلزال.', '2023-09-14 14:10:00', './image/0.png ', '     وأوضحت الوزارة، في بلاغ لها، اليوم الخميس، أنه “على إثر تداول بعض الأخبار، عبر شبكات التواصل الاجتماعي،', 2),
	(9, ' وأهابت وزارة التجهيز والماء بجميع المواطنات والمواطنين تحري الدقة والمصداقية في ما يتم تداوله من أخبار حول تداعيات الزلزال.', '  وأهابت وزارة التجهيز والماء بجميع المواطنات والمواطنين تحري الدقة والمصداقية في ما يتم تداوله من أخبار حول تداعيات الزلزال.', '2023-09-14 14:12:00', './image/1.png ', '     وأهابت وزارة التجهيز والماء بجميع ', 2),
	(11, 'ت مواقع التواصل الاجتماعي، و لحظة تبرعه بالدم لفائدة ضحايا الزلزال و عناقه للمصابين و تقبيله لرؤوس المرضى و اطمئنانه على رعيته، لقطات ستبقى خالدة في أذهان و قلوب كل المغاربة لقائد عظيم، لملك مواطن، لإنسان خدوم، لمغربي نموذج.', 'ت مواقع التواصل الاجتماعي، و لحظة تبرعه بالدم لفائدة ضحايا الزلزال و عناقه للمصابين و تقبيله لرؤوس المرضى و اطمئنانه على رعيته، لقطات ستبقى خالدة في أذهان و قلوب كل المغاربة لقائد عظيم، لملك مواطن، لإنسان خدوم، لمغربي نموذج.', '2023-09-14 14:32:00', './image/44386.png ', '     وزارة التجهيز والماء تنفي تضرر ', 2),
	(22, ' مستشفى الرازي ببرشيد،محطة عبور لا غير !!!', '  مستشفى الرازي ببرشيد،محطة عبور لا غير !!!\r\nبعد إحداث عمالة إقليم برشيد،تفاءلت الساكنة أن قطاع الصحة سيعرف تحديثا وتوسيعا لمستشفى الرازي الذي أصبح إقليميا بقدرة قادر أمام غياب مؤشرات للتقييم والتجويد، لتتراجع الخدمات الصحية والطبية بشكل ملفت أمام طلبات وحاجيات 22جماعة ترابية يضمها إقليم برشيد،بل أصبح هذا المستشفى لا يلبي حتى حاجيات مدينة برشيد الحضرية وليتأكد وبالملموس أن المستشفى الإقليمي لبرشيد أصبح محطة عبور نحو مستشفيات سطات والدارالبيضاء ،أمام عجزه الكبير عن استقبال و تقديم الخدمات الطبية والعلاجية للمرضى والجرحى والولادة بمدينة برشيد\r\n وباقي ساكنة 22جماعة ترابية  !!! \r\nفالمستشفي "الإقليمي" الرازي ببرشيد يضم فقط 45سريرا لساكنة يفوق عدد سكانها 500 ألف نسمة،ويتوفر على طبيبة واحدة لأمراض العيون وطبيب واحد للعظام والمفاصل وافتقار المستشفى لأطباء النساء والتوليد بعد مغادرة أغلب طبيبات التوليد واستفادتهن من التقاعد النسبي للإشتغال بالمستشفيات الخاصة ببرشيد،مما يضطر غالبية حالات الولادة توجه نحو سطات أو الدارالبيضاء وغالبا ما تعود بدعوى سهولة عملية التوليد ولا تحتاج الى التوجيه خارج برشيد.\r\nكما أن مستشفى الرازي يقف عاجزا أمام بعض حالات الجراحة وفحوصات الراديو الطبي والسكانير الذي ولد معطلا مما يضطر المرضى رغم العوز والفقر لإجراء الفحوصات بالمستشفيات الخصوصية ببرشيد وبأثمنة باهضة ! إضافة الى بعد تواريخ المواعيد الطبية التي قد تصل الى السنة دون مراعاة الوضع الصحي العاجل للمرضى !\r\nكما يفتقر المستشفى "الإقليمي" الرازي ببرشيد الى قاعة للإنعاش وعدم تجهيز وتأهيل قاعة المستعجلات مما يقف عاجزا أمام تقديم الخدمات الطبية المستعجلة والآنية. كما يعاني المستشفى من النقص الكبير في سيارات الإسعاف مما يضطر عائلات المرضى والجرحى الى الإستعانة بسيارات الإسعاف الخصوصية الباهضة الثمن.\r\nفساكنة مدينة برشيد و جماعاتها الترابية ال 22 في حاجة ماسة ومستعجلة الى مستشفى إقليمي كبير وبجميع التخصصات وبموارد بشرية طبية وتمريضية كافية وبلوجستيك طبي متوفر، وبخدمات طبية وعلاجية هادفة وفاعلة.ولن يتأتى هذا الحلم إلا بتظافر جهود كافة مسؤولي ومكونات وفعاليات مدينة برشيد من نواب ومستشارين برلمانيين عن دائرة برشيد ومجلس إقليمي ببرشيد والمجلس الجماعي لبرشيد ومنذوبية قطاع الصحة ببرشيد ووزارة الصحة وهيئات المجتمع المدني ببرشيد والإعلام المحلي...رغبة في توسعة وتأهيل مستشفى الرازي ببرشيد حتى يرقى لمواصفات مستشفى إقليمي كمطلب مستعجل في انتظار إحداث مستشفى كبير بمواصفات طبية عالية تليق وتلبي كافة حاجيات إقليم برشيد.\r\n            بقلم الأستاذ نورالدين مزضار .', '2023-09-18 16:15:00', './image/photo_5785343539696222334_x.jpg ', '     مستشفى الرازي ببرشيد،محطة عبور لا غير !!!', 3),
	(23, ' هل يترك الزلزال آثارا نفسية سيئة؟!.. خبير نفسي يجيب.. ! \r\n', '  هل يترك الزلزال آثارا نفسية سيئة؟!.. خبير نفسي يجيب.. ! \r\n\r\nبقلم/ حنان الطيبي\r\n \r\nبدأت ساكنة مدينة فاس تحاول شيئا فشيئا العودة إلى الحياة الطبيعية بعد زلزال يوم الجمعة الذي اتى على العديد من المدن  كمراكش والدار البيضاء والرباط وورزازات وغيرها من المدن المغربية، وكذا على مجموعة من الدول مثل كاليفورنيا وايسلاندا إذ انطلقت الموجة الزلزالية العالمية من ايسلاندا و نيوزيلاندا ثم امريكا الشمالية و الجنوبية ثم شمال افريقيا وصلت بعد 20 دقيقة الى البرتغال و اسبانيا. \r\nوفي حديث ل"صدى المواطن" مع الباحث في علم النفس "علي الميسوري"، حول الأثر النفسي الذي قد يتركه زلزال مفاجئ وعنيف لدى من خاض تجربة مماثلة، أوضح هذا الأخير أنه من الممكن أن تترك الزلازل آثاراً نفسية طويلة الأمد على الأشخاص، مثل اضطرابات ما بعد الصدمة، ويحتاج بعضهم إلى دعم نفسي لمساعدتهم لتخطي خوض تجربة مماثلة ، كما اكد الآثار النفسية تاتي على مراحل مرتبط بعضها ببعض لدى اغلبية الأشخاص، ففي المرحلة الأولى من الطبيعي جدا سيطرة الهلع والخوف على الاشخاص كردة فعل أولية عند الإحساس برجات الاهتزازات المفاجئة وهو ما يعرف ب "الصدمة"، حيث يحاول العقل استيعاب الأمر في البداية، إلا ان هذه الصدمة سرعان ما يصاحبها الذعر في الثواني الأولى من الزلزال لكن تبقى بنسب متفاوتة حسب قدرة التحمل واختلاف الشخصيات، ثم مباشرة البحث عن مكان آمن.\r\nثم تاتي المرحلة الثانية وهي ما يسمى بالقلق والقلق المستمر، فبعد انتهاء الزلزال، يمكن أن يستمر القلق لدى الأشخاص بشأن تكرار الزلازل او إقبال اهتزازات ارتدادية، وكذا القلق من آثارها المستقبلية على حياتهم وممتلكاتهم… \r\n لذلك يؤكد الباحث في علم النفس، أنه من الضروري إخضاع الأشخاص الذين تركت لديهم الزلازل آثارا سلبية نفسية، إلى جلسات نفسية بهدف الدعم النفسي وتخطي التجربة السيئة خصوصا لدى بعض الحالات ممن فقدوا اهاليهم او ممتلكات قيمة أو ممن تولدت لديهم عقدة الإحساس بالذنب على ما فقدوه. \r\nوبين هذا وذاك تحكي سارةل، أم لطفلين يوسف وليلى، والتي تعيش في بيت صغير بقرية بضواحي مدينة فاس، حول تجربتها مع زلزال يوم الجمعة، أنها كانت تحاول إخلاد طفليها في السرير لتحس بهزة خفيفة، اعتقدت اول الامر أنها دوخة اصابتها بسبب الإجهاد والإعياء، لكن سرعان ما أدركت سارة أنها هزة زلزال خصوصا ان كل الأشياء حولها كانت تتحرك وتتساقط بسرعة، أدركت سارة أنها يجب عليها حماية أطفالها بأي ثمن. بالرغم من خوفها والضرر الذي لحق بالمنزل، ثم أمسكت بيد يوسف وأخذت ليلى في حضنها وانطلقت نحو الباب الخارجي. وجدت سارة اهالي القرية يهرولون جميعا خارج بيوتهم، ثم توجهت مباشرة إلى المكان الأكثر أمانًا الذي عرفته، وهو حقل ذرة كان جنوب القرية، وهناك قضت سارة ليلة كاملة بجانب طفليها حيث تبعها اهل القرية جميعهم إلى ان اطلق النهار اول إشراقة له.\r\nاما عائلة العمراني فيحكي افرادها أنهم كانوا جميعًا يجلسون حول الطاولة في غرفة الطعام، يتناولون وجبة العشاء وكان الحديث يدور حول أحداث اليوم وخطط عطلة نهاية الأسبوع المقبلة، وفي لحظة قصيرة جدا شعر الجميع بارتجاج خفيف في الأرض، وسمعوا صوت رنين خفيف. بدأوا يتساءلون عما إذا كان هذا زلزالا ام ماذا لكن الأم صرخت بانه زلزال فعلا وامرت الجميع بالتوجه نحو باب البيت، ثم سرعان ما بدأت الأمور تتصاعد بسرعة لتزداد حدة الارتجاجات، وبدأ بعض الأثاث يتساقط من الرفوف…، وسرعان ما اندلع الذعر في قلوب أفراد العائلة، ولكن يقول الأب:( حافظنا على هدوئنا بهدف عدم التوتر والبقاء آمنين)، لكن يحكي الأب أنه على الرغم من صراخ الأم للتوجه إلى باب المنزل تذكر أنه يجب عليهم البقاء تحت الطاولة الكبيرة حتى ينتهي الزلزال.\r\nويستطرد الأب قائلا: (شعرت بالقلق وحاولت تهدئة أفراد عائلتي… قمت بدور القائد وأخذت الإجراءات الضرورية لحمايتهم.. استمر الزلزال لحوالي نصف دقيقة وكأنه عمر بأكمله.. وبعد انتهائه، قمنا بفحص المنزل وتقييم الأضرار.. ولحسن الحظ، لم تكن هناك أضرار كبيرة، والحمد لله اننا جميعًا بخير). \r\nلعل زلزال يوم الجمعة ترك الأثر الكبير لدى المغاربة جميعا، سواء اولئك الذين قضوا الليلة في بيوتهم او اولئك الذين قضوها بالشارع العام، لكن من المؤكد أن اولئك الذين قضوها في قبورهم قضوها في رياض الجنة أحياء يرزقون فرحين بالشهادة\r\n', '2023-09-18 16:35:00', './image/16fc81a4-38eb-44c1-84dd-8faec372b9c2.jpg ', '     هل يترك الزلزال آثارا نفسية سيئة؟!.. خبير نفسي يجيب.. ! \r\n', 3),
	(24, ' "ﺗرأس ﺻﺎﺣب اﻟﺟﻼﻟﺔ اﻟﻣﻠك ﻣﺣﻣد اﻟﺳﺎدس، ﻧﺻره ﷲ، ﻣرﻓوﻗﺎ ﺑﺻﺎﺣب اﻟﺳﻣو اﻟﻣﻠﻛﻲ وﻟﻲ اﻟﻌﮭد اﻷﻣﯾر ﻣوﻻي اﻟﺣﺳن، ﺑﻌد زوال اﻟﺳﺑت 9 ﺷﺗﻧﺑر 2023 ﺑﺎﻟﻘﺻر اﻟﻣﻠﻛﻲ ﺑﺎﻟرﺑﺎط، ﺟﻠﺳﺔ ﻋﻣل ﺧﺻﺻت ﻟﺑﺣث اﻟوﺿﻊ ﻓﻲ أﻋﻘﺎب اﻟزﻟزال المؤلم، اﻟذي وﻗﻊ يوم اﻟﺟﻣﻌﺔ 8 شتنبر، واﻟذي ﺧﻠف ﺧﺳﺎﺋر ﺑﺷرﯾﺔ كبيرة وﻣﺎدﯾﺔ ﻓﻲ العديد ﻣن ﺟﮭﺎت اﻟﻣﻣﻠﻛﺔ.\r\n', '  "ﺗرأس ﺻﺎﺣب اﻟﺟﻼﻟﺔ اﻟﻣﻠك ﻣﺣﻣد اﻟﺳﺎدس، ﻧﺻره ﷲ، ﻣرﻓوﻗﺎ ﺑﺻﺎﺣب اﻟﺳﻣو اﻟﻣﻠﻛﻲ وﻟﻲ اﻟﻌﮭد اﻷﻣﯾر ﻣوﻻي اﻟﺣﺳن، ﺑﻌد زوال اﻟﺳﺑت 9 ﺷﺗﻧﺑر 2023 ﺑﺎﻟﻘﺻر اﻟﻣﻠﻛﻲ ﺑﺎﻟرﺑﺎط، ﺟﻠﺳﺔ ﻋﻣل ﺧﺻﺻت ﻟﺑﺣث اﻟوﺿﻊ ﻓﻲ أﻋﻘﺎب اﻟزﻟزال المؤلم، اﻟذي وﻗﻊ يوم اﻟﺟﻣﻌﺔ 8 شتنبر، واﻟذي ﺧﻠف ﺧﺳﺎﺋر ﺑﺷرﯾﺔ كبيرة وﻣﺎدﯾﺔ ﻓﻲ العديد ﻣن ﺟﮭﺎت اﻟﻣﻣﻠﻛﺔ.\r\nوﻓﻲ ھذا اﻟﺻدد، اﺳﺗﻌرض اﻟﻣﺳؤوﻟون اﻟﺣﺎﺿرون ﺑﯾن ﯾدي ﺻﺎﺣب اﻟﺟﻼﻟﺔ، ﺣﻔظه ﷲ، آﺧر اﻟﺗطورات اﻟﺗﻲ ﺗﻌرﻓﮭﺎ اﻟﻌﻣﺎﻻت واﻷﻗﺎﻟﯾم اﻟﻣﺗﺿررة، وﻻ ﺳﯾﻣﺎ ﻋﻠﻰ ﻣﺳﺗوى ﺑﻌض اﻟمناطق اﻟﺗﻲ ﺗﻌذر اﻟوﺻول إﻟﯾﮭﺎ ﻟﯾﻼ، واﻟﺗﻲ ﻟم ﯾﺗم ﺗﺣدﯾد اﻟوﺿﻊ اﻟﻘﺎﺋم ﺑﮭﺎ، واﻟﺷروع ﻓﻲ ﻋﻣﻠﯾﺎت اﻹﻧﻘﺎذ إﻻ عند مطلع النهار.\r\nوﺧﻼل ھذه اﻟﺟﻠﺳﺔ، رﻓﻊ اﻟﻣﺳؤوﻟون اﻟﺣﺎﺿرون ﻟﻠﻌﻧﺎﯾﺔ اﻟﻣﻠﻛﯾﺔ اﻟﺳﺎﻣﯾﺔ ﺗﻔﺎﺻﯾل اﻹﺟراءات اﻟﺗﻲ ﺗم اﺗﺧﺎذھﺎ ﻣن أﺟل اﻟﺗﻌﺎﻣل اﻟﺳرﯾﻊ ﻣﻊ ھذه اﻟﻛﺎرﺛﺔ اﻟطﺑﯾﻌﯾﺔ اﻟﻛﺑرى.\r\nوﻗد ھﻣت ھذه اﻹﺟراءات اﻻﺳﺗﻌﺟﺎﻟﯾﺔ، اﻟﺗﻲ ﺷﻛﻠت ﻣوﺿوع ﺗﻌﻠﯾﻣﺎت ﺟﻼﻟﺔ اﻟﻣﻠك وﺗﺗبعه اﻟداﺋم ﻣﻧذ اﻟﻠﺣظﺎت اﻷوﻟﻰ اﻟﺗﻲ أﻋﻘﺑت اﻟزﻟزال، واﻟﺗﻲ ﺷﮭدت ﺗدﺧل اﻟﻘوات اﻟﻣﺳﻠﺣﺔ اﻟﻣﻠﻛﯾﺔ، واﻟﺳﻠطﺎت اﻟﻣﺣﻠﯾﺔ، وﻣﺻﺎﻟﺢ ﺣﻔظ اﻟﻧظﺎم، وﻓرق اﻟوﻗﺎﯾﺔ اﻟﻣدﻧﯾﺔ، وﻛذا اﻟﻘطﺎﻋﺎت اﻟوزارﯾﺔ اﻟﻣﻌﻧﯾﺔ، ﺑﺎﻷﺳﺎس:\r\n- ﺗﻌزﯾز اﻟوﺳﺎﺋل وﻓرق اﻟﺑﺣث واﻹﻧﻘﺎذ ﻣن أﺟل ﺗﺳرﯾﻊ ﻋﻣﻠﯾﺔ إﻧﻘﺎذ وإﺟﻼء اﻟﺟرﺣﻰ،\r\n- ﺗزوﯾد اﻟﻣﻧﺎطق اﻟﻣﺗﺿررة ﺑﺎﻟﻣﺎء اﻟﺻﺎﻟﺢ ﻟﻠﺷرب،\r\n- ﺗوزﯾﻊ ﺣﺻص ﻏذاﺋﯾﺔ وﺧﯾﺎم وأﻏطﯾﺔ ﻋﻠﻰ اﻟﻣﻧﻛوﺑﯾن،\r\n- اﻻﺳﺗﺋﻧﺎف اﻟﺳرﯾﻊ ﻟﻠﺧدﻣﺎت اﻟﻌﻣوﻣﯾﺔ.\r\nوﺗﺟدر اﻹﺷﺎرة ﻓﻲ ھذا اﻟﺻدد، إﻟﻰ أنه ﺗﻧﻔﯾذا ﻟﻠﺗﻌﻠﯾﻣﺎت اﻟﺳﺎﻣﯾﺔ ﻟﺻﺎﺣب اﻟﺟﻼﻟﺔ اﻟﻣﻠك ﻣﺣﻣد اﻟﺳﺎدس، اﻟﻘﺎﺋد اﻷﻋﻠﻰ ورﺋﯾس أرﻛﺎن اﻟﺣرب اﻟﻌﺎﻣﺔ ﻟﻠﻘوات اﻟﻣﺳﻠﺣﺔ اﻟﻣﻠﻛﯾﺔ، ﻧﺷرت اﻟﻘوات اﻟﻣﺳﻠﺣﺔ اﻟﻣﻠﻛﯾﺔ، ﺑﺷﻛل ﻣﺳﺗﻌﺟل، وﺳﺎﺋل ﺑﺷرﯾﺔ وﻟوﺟﯾﺳﺗﯾﺔ ﻣﮭﻣﺔ، ﺟوﯾﺔ وﺑرﯾﺔ، وﻛذا وﺣدات ﺗدﺧل ﻣﺗﺧﺻﺻﺔ ﻣﻛوﻧﺔ ﻣن ﻓرق ﻟﻠﺑﺣث واﻹﻧﻘﺎذ، وﻣﺳﺗﺷﻔﻰ طﺑﻲ ﺟراﺣﻲ ﻣﯾداﻧﻲ.\r\nوﻓﻲ ھذا اﻹطﺎر، أﻋطﻰ ﺻﺎﺣب اﻟﺟﻼﻟﺔ ﺗﻌﻠﯾﻣﺎته اﻟﺳﺎﻣﯾﺔ ﺑﮭدف ﻣواﺻﻠﺔ ﻛﺎﻓﺔ أﻋﻣﺎل اﻹﻧﻘﺎذ ﺑﺷﻛل ﻋﺎﺟل ﻋﻠﻰ اﻟﺻﻌﯾد اﻟﻣﯾداﻧﻲ، وﻛذا ﻣن أﺟل :\r\n- اﻹﺣداث اﻟﻔوري ﻟﻠﺟﻧﺔ ﺑﯾن وزارﯾﺔ ﻣﻛﻠﻔﺔ ﺑوﺿﻊ ﺑرﻧﺎﻣﺞ اﺳﺗﻌﺟﺎﻟﻲ ﻹﻋﺎدة ﺗﺄھﯾل وﺗﻘدﯾم اﻟدﻋم ﻹﻋﺎدة ﺑﻧﺎء اﻟﻣﻧﺎزل اﻟﻣدﻣرة ﻋﻠﻰ ﻣﺳﺗوى اﻟﻣﻧﺎطق اﻟﻣﺗﺿررة ﻓﻲ أﻗرب اﻵﺟﺎل.\r\n- اﻟﺗﻛﻔل ﺑﺎﻷﺷﺧﺎص ﻓﻲ وﺿﻌﯾﺔ ﺻﻌﺑﺔ، ﺧﺻوﺻﺎ اﻟﯾﺗﺎﻣﻰ واﻷﺷﺧﺎص ﻓﻲ وﺿﻌﯾﺔ ھﺷﺔ.\r\n- التكفل الفوري بكافة الأشخاص بدون مأوى جراء الزلزال، لاسيما في ما يرتبط بالإيواء والتغذية وكافة الاحتياجات الأساسية.\r\n- ﺗﺷﺟﯾﻊ اﻟﻔﺎﻋﻠﯾن اﻻﻗﺗﺻﺎدﯾﯾن ﺑﮭدف اﻻﺳﺗﺋﻧﺎف اﻟﻔوري ﻟﻸﻧﺷطﺔ ﻋﻠﻰ ﻣﺳﺗوى اﻟﻣﻧﺎطق اﻟﻣﻌﻧﯾﺔ.\r\n- ﻓﺗﺢ ﺣﺳﺎب ﺧﺎص ﻟدى اﻟﺧزﯾﻧﺔ وﺑﻧك اﻟﻣﻐرب ﺑﮭدف ﺗﻠﻘﻲ اﻟﻣﺳﺎھﻣﺎت اﻟﺗطوﻋﯾﺔ اﻟﺗﺿﺎﻣﻧﯾﺔ ﻟﻠﻣواطﻧﯾن واﻟﮭﯾﺋﺎت اﻟﺧﺎﺻﺔ واﻟﻌﻣوﻣﯾﺔ.\r\n- اﻟﺗﻌﺑﺋﺔ اﻟﺷﺎﻣﻠﺔ ﻟﻣؤﺳﺳﺔ ﻣﺣﻣد اﻟﺧﺎﻣس ﻟﻠﺗﺿﺎﻣن، ﺑﺟﻣﯾﻊ ﻣﻛوﻧﺎﺗﮭﺎ، ﻣن أﺟل ﺗﻘدﯾم اﻟدﻋم وﻣواﻛﺑﺔ اﻟﻣواطﻧﯾن ﻓﻲ اﻟﻣﻧﺎطق اﻟﻣﺗﺿررة.\r\n- تشكيل احتياطات ومخزون للحاجيات الأولية (أدوية، خيام، أسرة، مواد غذائية ..) على مستوى كل جهة من المملكة من أجل مواجهة كل أشكال الكوارث\r\nوﺗﻘرر، أﯾﺿﺎ، اﻹﻋﻼن ﻋن ﺣداد وطﻧﻲ ﻟﻣدة ﺛﻼﺛﺔ أﯾﺎم، ﻣﻊ ﺗﻧﻛﯾس اﻷﻋﻼم اﻟوطﻧﯾﺔ ﻓوق ﺟﻣﯾﻊ اﻟﻣﺑﺎﻧﻲ اﻟﻌﻣوﻣﯾﺔ.\r\nﻣن ﺟﮭﺔ أﺧرى، أﻋطﻰ ﺟﻼﻟﺔ اﻟﻣﻠك أﻣﯾر اﻟﻣؤﻣﻧﯾن ﺗﻌﻠﯾﻣﺎته اﻟﺳﺎﻣﯾﺔ ﻟوزﯾر اﻷوﻗﺎف واﻟﺷؤون اﻹﺳﻼﻣﯾﺔ ﻷداء ﺻﻼة اﻟﻐﺎﺋب، ﺑﻣﺟﻣوع ﻣﺳﺎﺟد اﻟﻣﻣﻠﻛﺔ ﺗرﺣﻣﺎ ﻋﻠﻰ أرواح ﺿﺣﺎﯾﺎ ھذه اﻟﻛﺎرﺛﺔ اﻟطﺑﯾﻌﯾﺔ.\r\nوأﻋرب ﺟﻼﻟﺔ اﻟﻣﻠك، أﯾﺿﺎ، ﻋن ﺧﺎﻟص ﺗﺷﻛرات اﻟﻣﻣﻠﻛﺔ اﻟﻣﻐرﺑﯾﺔ ﻟﻠﻌدﯾد ﻣن اﻟﺑﻠدان اﻟﺷﻘﯾﻘﺔ واﻟﺻدﯾﻘﺔ، اﻟﺗﻲ ﻋﺑرت ﻋن ﺗﺿﺎﻣﻧﮭﺎ ﻣﻊ اﻟﺷﻌب اﻟﻣﻐرﺑﻲ ﻓﻲ ھذا اﻟظرف، واﻟﺗﻲ ﻋﺑرت اﻟﻌدﯾد ﻣﻧﮭﺎ ﻋن اﺳﺗﻌدادھﺎ ﻟﺗﻘدﯾم اﻟﻣﺳﺎﻋدة ﻓﻲ ھذه اﻟظروف اﻻﺳﺗﺛﻧﺎﺋﯾﺔ.\r\nﺣﺿر ﺟﻠﺳﺔ اﻟﻌﻣل ھﺎته اﻟﺳﺎدة ﻋزﯾز أﺧﻧوش رﺋﯾس اﻟﺣﻛوﻣﺔ، وﻋﺑد اﻟواﻓﻲ ﻟﻔﺗﯾت وزﯾر اﻟداﺧﻠﯾﺔ، وﺧﺎﻟد آﯾت اﻟطﺎﻟب وزﯾر اﻟﺻﺣﺔ واﻟﺣﻣﺎﯾﺔ اﻻﺟﺗﻣﺎﻋﯾﺔ.\r\nﻛﻣﺎ ﺣﺿر ھذه الجلسة اﻟﻔرﯾق أول ﻣﺣﻣد ﺑرﯾظ اﻟﻣﻔﺗش اﻟﻌﺎم ﻟﻠﻘوات اﻟﻣﺳﻠﺣﺔ اﻟﻣﻠﻛﯾﺔ وﻗﺎﺋد اﻟﻣﻧطﻘﺔ اﻟﺟﻧوﺑﯾﺔ، واﻟﻔرﯾق أول ﻣﺣﻣد هرﻣو، ﻗﺎﺋد اﻟدرك اﻟﻣﻠﻛﻲ، واﻟطﺑﯾب اﻟﻠواء ﻣﺣﻣد ﻋﺑﺎر، ﻣﻔﺗش ﻣﺻﻠﺣﺔ اﻟﺻﺣﺔ اﻟﻌﺳﻛرﯾﺔ، واﻟﻌﻣﯾد إﺣﺳﺎن ﻟطﻔﻲ اﻟﻣدﯾر اﻟﻌﺎم ﻟﻠوﻗﺎﯾﺔ اﻟﻣدﻧﯾﺔ، وﻋﺑد اﻟﻠطﯾف ﺣﻣوﺷﻲ، اﻟﻣدﯾر اﻟﻌﺎم ﻟﻸﻣن اﻟوطﻧﻲ وﻣراﻗﺑﺔ اﻟﺗراب اﻟوطﻧﻲ، وﻣﺣﻣد اﻷزﻣﻲ، ﻣﻧﺳق ﻣؤﺳﺳﺔ ﻣﺣﻣد اﻟﺧﺎﻣس ﻟﻠﺗﺿﺎﻣن".', '2023-09-18 16:35:00', './image/hokoma.jpeg ', '     "ﺗرأس ﺻﺎﺣب اﻟﺟﻼﻟﺔ اﻟﻣﻠك ﻣﺣﻣد اﻟﺳﺎدس، ﻧﺻره ﷲ، ﻣرﻓوﻗﺎ ﺑﺻﺎﺣب اﻟﺳﻣو اﻟﻣﻠﻛﻲ وﻟﻲ اﻟﻌﮭد اﻷﻣﯾر ﻣوﻻي اﻟﺣﺳن، \r\n', 2),
